// ================
// DEPENDENCIES
// ================
import React , { Component } from 'react';
import Header from './components/Header';
import EntryForm from './components/EntryForm';
import AllEntries from './components/AllEntries';

// =================
// COMPONENT
// =================
class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            currentView: 'all',
            newerEntries: [],
            oldEntries: []
        }
        // method binding
        this.handleView = this.handleView.bind(this);
        this.fetchEntries = this.fetchEntries.bind(this);
        this.sortEntries = this.sortEntries.bind(this);

    }

    // method to change views
    handleView(view) {
        this.setState( { currentView: view } )
    }

    // method to retreive data from API on load
    componentDidMount() {

    }

    // API call
    fetchEntries() {
        fetch()
        .then(data => data.json())
        .then(jData => {
            console.log(jData);
        })
    }

    // method to sort seperate data into arrays
    sortEntries (entries) {
    let newerEntries = []
    let oldEntries = []
        entries.forEach((entry) => {
            if (entry.status == 'old') {
                oldEntries.push(entry)
            } else {
                newerEntries.push(entry)
            }
        })
        this.setEntries(newerEntries, oldEntries)
    }

    // method to set state
    setEntries(newer, old) {
        this.setState({
            newerEntries: newer,
            oldEntries: old
        })
    }

    render() {
        return (
          <div className="App">
            <Header
            currentView={this.state.currentView}
            handleView={this.handleView}
            />
            <EntryForm />
            <AllEntries currentView={this.state.currentView} />

          </div>
        );
    }
}

// ================
// EXPORT
// ================
export default App;
